// src/App.js
import React, { useEffect  ,useState} from "react";
import Inventory from "./components/Inventory";
import { fetchItems } from "./redux/api/inventory";
import { useDispatch } from "react-redux";
import { Route, Routes } from 'react-router-dom';
import { Navbar } from "./components/Navbar";
const App = () => {
  const [theme, setTheme] = useState('light'); // Default theme
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchItems());
      // Get saved theme from localStorage
      const savedTheme = localStorage.getItem('mode');
      if (savedTheme) {
        setTheme(savedTheme); // Apply saved theme
      } else {
        localStorage.setItem('mode', 'light'); // Default theme if no saved theme
      }
  }, [dispatch]);

  useEffect(() => {
    // Set the theme on the document body when it changes
    document.body.dataset.bsTheme = theme;
    localStorage.setItem('mode', theme); // Save theme in localStorage
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };
  return (
    <div>
      <Navbar toggleTheme={toggleTheme} theme={theme}/>
        <Routes>

          <Route path="/" element={<Inventory />} />
        </Routes>
    </div>
  );
};

export default App;
