import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = "http://localhost:5000/api/inventory";

export const fetchItems = createAsyncThunk("inventory/fetchItems", async () => {
  const response = await axios.get(API_URL);
  return response.data;
});

export const addItem = createAsyncThunk("inventory/addItem", async (item) => {
  const response = await axios.post(API_URL, item);
  return response.data;
});

export const deleteItem = createAsyncThunk("inventory/deleteItem", async (id) => {
  await axios.delete(`${API_URL}/${id}`);
  return id;
});

const inventorySlice = createSlice({
  name: "inventory",
  initialState: { items: [], status: "idle" },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchItems.fulfilled, (state, action) => { state.items = action.payload; })
      .addCase(addItem.fulfilled, (state, action) => { state.items.push(action.payload); })
      .addCase(deleteItem.fulfilled, (state, action) => { state.items = state.items.filter(item => item.id !== action.payload); });
  },
});

export default inventorySlice.reducer;
