import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchItems, addItem, deleteItem } from "../redux/slices/InventorySlice";
import { Container, Table, Button, Form } from "react-bootstrap";

const Inventory = () => {
  const dispatch = useDispatch();
  const { items } = useSelector(state => state.inventory);
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState(1);

  useEffect(() => { dispatch(fetchItems()); }, [dispatch]);

  const handleAddItem = () => {
    dispatch(addItem({ item_name: itemName, quantity }));
    setItemName("");
    setQuantity(1);
  };

  return (
    <Container className="mt-4">
      <h2 className="text-center">إدارة الجرد</h2>
      <Form className="d-flex gap-2 mb-3">
        <Form.Control type="text" placeholder="اسم المنتج" value={itemName} onChange={(e) => setItemName(e.target.value)} />
        <Form.Control type="number" placeholder="الكمية" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} />
        <Button variant="primary" onClick={handleAddItem}>إضافة</Button>
      </Form>

      <Table striped bordered hover>
        <thead><tr><th>#</th><th>المنتج</th><th>الكمية</th><th>إجراء</th></tr></thead>
        <tbody>{items.map((item, index) => (
          <tr key={item.id}><td>{index + 1}</td><td>{item.item_name}</td><td>{item.quantity}</td>
            <td><Button variant="danger" size="sm" onClick={() => dispatch(deleteItem(item.id))}>حذف</Button></td>
          </tr>
        ))}</tbody>
      </Table>
    </Container>
  );
};

export default Inventory;
