import { useState } from "react";
import { Link } from "react-router-dom";

export const InventoryPost = () => {
    const { user } = useSelector((store) => store.data)
  const [loading, setLoading] = useState(false);
  const [name, setName] = useState(user ? user.name : "")
  return (
    <div className="main-content-wrapper">
      <div className="container">
        <div className="row">
          <div className="col-lg-9 col-md-8 col-12">
            <div className="py-6 p-md-6 p-lg-10">
              <div className="mb-6">
                <h2 className="mb-0">Add Product</h2>
              </div>
              <div>
                <div className="row">
                  <div className="col-lg-5">
                    <div className="mb-3">
                      <label className="form-label">Name</label>
                      <input
                        type="text"
                        className="form-control border"
                        placeholder="Enter Your Name"
                        /* onChange={(e) => setName(e.target.value)} value={name} */
                      />
                    </div>

                    <div className="mb-3">
                      <button
                        className="btn btn-primary ml-2" /*  onClick={handleSubmit} */
                      >
                        {!loading && (
                          <span className="indicator-label"> Save Details</span>
                        )}
                        {loading && (
                          <span className="indicator-progress">
                            Please wait...
                            <span className="spinner-border spinner-border-sm align-middle ms-2"></span>
                          </span>
                        )}
                      </button>
                      <button
                        className="btn btn-primary" /*  onClick={handleSubmit} */
                      >
                        {!loading && (
                          <span className="indicator-label"> Save Details</span>
                        )}
                        {loading && (
                          <span className="indicator-progress">
                            Please wait...
                            <span className="spinner-border spinner-border-sm align-middle ms-2"></span>
                          </span>
                        )}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <hr className="my-10 border" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
